function toggleSidebar(){const sidebar=document.getElementById("sidebar");const isMobile=window.innerWidth<768;if(isMobile){sidebar.classList.toggle("-translate-x-full")}else{if(sidebar.classList.contains("w-64")){sidebar.classList.remove("w-64");sidebar.classList.add("sidebar-hidden")}else{sidebar.classList.remove("sidebar-hidden");sidebar.classList.add("w-64")}}}
function closeMobileSidebar(){const sidebar=document.getElementById("sidebar");if(window.innerWidth<768){sidebar.classList.add("-translate-x-full")}}
function handleResize(){const sidebar=document.getElementById("sidebar");const isMobile=window.innerWidth<768;if(isMobile){sidebar.classList.add("-translate-x-full");sidebar.classList.remove("sidebar-hidden");sidebar.classList.add("w-64")}else{sidebar.classList.remove("-translate-x-full");sidebar.classList.remove("sidebar-hidden");sidebar.classList.add("w-64")}}
document.getElementById("desktop-sidebar-toggle").addEventListener("click",toggleSidebar);document.getElementById("mobile-sidebar-toggle").addEventListener("click",function(){const sidebar=document.getElementById("sidebar");sidebar.classList.toggle("-translate-x-full")});document.getElementById("sidebar-toggle").addEventListener("click",function(){const sidebar=document.getElementById("sidebar");sidebar.classList.toggle("-translate-x-full")});document.querySelectorAll(".dropdown-btn").forEach((button)=>{button.addEventListener("click",function(){const dropdown=this.nextElementSibling;const icon=this.querySelector(".fa-chevron-down");dropdown.classList.toggle("hidden");icon.classList.toggle("rotate-180")})});document.getElementById("dark-mode-toggle").addEventListener("click",function(){document.documentElement.classList.toggle("dark");if(document.documentElement.classList.contains("dark")){localStorage.setItem("darkMode","enabled")}else{localStorage.setItem("darkMode","disabled")}});document.addEventListener("click",function(event){const sidebar=document.getElementById("sidebar");const mobileToggle=document.getElementById("mobile-sidebar-toggle");const desktopToggle=document.getElementById("desktop-sidebar-toggle");if(window.innerWidth<768&&!sidebar.contains(event.target)&&!mobileToggle.contains(event.target)&&!sidebar.classList.contains("-translate-x-full")){sidebar.classList.add("-translate-x-full")}});document.addEventListener("DOMContentLoaded",function(){if(localStorage.getItem("darkMode")==="enabled"){document.documentElement.classList.add("dark")}
handleResize()});window.addEventListener("resize",handleResize)